body.script:
document.addEventListener("DOMContentLoaded", function () {
  const heroIds = ['hero-academic'];
  const header = document.querySelector("header");
  const offset = header ? header.offsetHeight : 100;

  // First pick which to show
  const randomId = heroIds[Math.floor(Math.random() * heroIds.length)];
  const visibleSection = document.getElementById(randomId);

  heroIds.forEach(id => {
    const el = document.getElementById(id);
    if (!el) return;

    if (id === randomId) {
      el.style.display = "block";
      el.style.height = null;
      el.style.overflow = null;
      el.style.opacity = null;
      el.style.pointerEvents = null;
      el.style.paddingTop = `${offset}px`;
    } else {
      if (id === 'hero-academic') {
        // Keep padding-top alive by not display:none
        el.style.height = "0";
        el.style.overflow = "hidden";
        el.style.opacity = "0";
        el.style.pointerEvents = "none";
      } else {
        el.style.display = "none";
      }
      el.style.paddingTop = null;
    }
  });
});
